mapper.select:
SELECT

FROM char_ conversation
WHERE memory_id = #{memoryId}

SELECT

FROM char_ conversation
WHERE user_id = #{userId}

    AND status = #{status}

ORDER BY update_time DESC

mapper.update:
UPDATE char_conversation
SET title = #{title}
WHERE memory_id = #{memoryId}

UPDATE char_conversation
SET status = #{status}
WHERE memory_id = #{memoryId}

mapper.select:
SELECT COUNT(1) > 0
FROM char_conversation
WHERE memory_id = #{memoryId}
AND user_id = #{userId}
